$cachedosyas=md5($_SERVER['REQUEST_URI']);
$cachedosyasi="cache/".$cachedosyas.".md5";
$ch=fopen($cachedosyasi,"w+");
fwrite($ch, ob_get_contents());
fclose($ch);
ob_end_flush();
